mapper.insert:
insert into t_user (id,
loginacct, userpswd,
username, email, createtime
)
values
(#{id,jdbcType=INTEGER}, #{loginacct,jdbcType=VARCHAR},
#{userpswd,jdbcType=CHAR},
#{username,jdbcType=VARCHAR},
#{email,jdbcType=VARCHAR}, #{createtime,jdbcType=CHAR}
)

mapper.select:
select id, loginacct, userpswd,
username, email, createtime
from t_user where loginacct=#{loginacct} and userpswd=#{userpswd}

select id, loginacct, userpswd, username, email, createtime from t_user
loginacct LIKE concat("%",#{queryUserVo.queryText},"%")
limit #{startIndex},#{queryUserVo.pageSize}
